+    m_alpha = (1.5 * M_PI) - m_yawZ_strich;
+    m_x_off = m_sensor_offset_x * sin(m_alpha);
+    m_y_off = m_sensor_offset_x * cos(m_alpha);
+
+    m_off0 = -m_x_off;
+    m_off90 = -m_y_off;
