+LocalisationControlNode::LocalisationControlNode()
+: Node("localisation_control")
+{
+  //needs to wait for mapsserver to get initalized
+  std::this_thread::sleep_for(2s);
+
+  //client
+  map_server_load_map = this->create_client<nav2_msgs::srv::LoadMap>(map_server_load_map_topic);   // /map_server/load_map
+  LoadMap * lp = new LoadMap(map_server_load_map);
+
+  m_control = new Control();
+  m_localisation = new Localisation(m_control);
+  m_navigation = new Navigation(lp);
+  //subscriber
+  subscriber_odom_ =
+    this->create_subscription<nav_msgs::msg::Odometry>(
+    "/odom", 1,
+    std::bind(&LocalisationControlNode::odom_callback, this, std::placeholders::_1));
+  subscriber_velocity_ = this->create_subscription<geometry_msgs::msg::Twist>(
+    "/cmd_vel", 1, std::bind(
+      &LocalisationControlNode::velocity_callback, this,
+      std::placeholders::_1));
+  //subscriber_state_est_=this->create_subscription<std_msgs::msg::Float64MultiArray>("/state_est", 1, std::bind(&LocalisationControlNode::state_est_callback, this, std::placeholders::_1));
+  subscriber_laserscan_ = this->create_subscription<sensor_msgs::msg::LaserScan>(
+    "/demo/laser/out", rclcpp::QoS(
+      rclcpp::SensorDataQoS()),
+    std::bind(&LocalisationControlNode::scan_callback, this, std::placeholders::_1));
+  //###########NEU##############
+
+  subscriber_amcl_pose_ = this->create_subscription<geometry_msgs::msg::PoseWithCovarianceStamped>(
+    "/amcl_pose", 1,
+    std::bind(&LocalisationControlNode::amcl_pose_callback, this, std::placeholders::_1));
+  subscriber_imu_ =
+    this->create_subscription<sensor_msgs::msg::Imu>(
+    "/imu/data",
+    rclcpp::QoS(rclcpp::SensorDataQoS()),
+    std::bind(&LocalisationControlNode::imu_callback, this, std::placeholders::_1));
+  //publisher
+  publisher_state_est_ = this->create_publisher<std_msgs::msg::Float64MultiArray>("/state_est", 10);
+  publisher_vel_ = this->create_publisher<geometry_msgs::msg::Twist>("/cmd_vel", 10);
+  publisher_slam_scan_ = this->create_publisher<sensor_msgs::msg::LaserScan>("/scan", 10);
